import React, { useEffect, useRef, useState } from "react";
import nextIcon from "../../images/next.png";
import backIcon from "../../images/back.png";
import indexCircle from "../../images/index-circle.svg";
import { Box, Button, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import ImageList from "@material-ui/core/ImageList";
import ImageListItem from "@material-ui/core/ImageListItem";
import Facebook from "../../images/Header_icons/headerSocialIcons/facebook.svg";
import Tripadvisor from "../../images/Header_icons/headerSocialIcons/tripadvisor_icon.svg";
import instaIcon from "../../images/Header_icons/headerSocialIcons/insta.svg";
import houserules_mobile from "../../images/houserules_mobile.png";
import headerLogoMobileSize from "../../images/headerLogoMobileSize.svg";
import Email from "../../images/Header_icons/headerSocialIcons/email.svg";
import Whatsapp from "../../images/Header_icons/headerSocialIcons/whats.svg";
import clsx from "clsx";
import intro from "../../videos/intro.mp4";
import { WithTransLate } from "../../translating/index";
//#region slider images

import slide1 from "../../images/homePageSlider/slide1.jpg";
import slide2 from "../../images/homePageSlider/slide2.jpg";
import slide3 from "../../images/homePageSlider/slide3.jpg";
import slide4 from "../../images/homePageSlider/slide4.jpg";
import slide5 from "../../images/homePageSlider/slide5.jpg";
import slide6 from "../../images/homePageSlider/slide6.jpg";

//#endregion

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "space-around",
    width: "81vw",
    margin: "-55px auto 0",
    padding: "0",
    [theme.breakpoints.up("md")]: {
      flexDirection: "row",
    },
  },
  sliderRoot: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
    position: "relative",
    width: `80vw`,
    height: "590px",
    [theme.breakpoints.down("md")]: {
      width: `87vw`,
    },
    [theme.breakpoints.down("xs")]: {
      width: "calc(0vw - 0px)",
    },
  },
  "@media(max-width:660px)": {
    root: {
      marginTop: "10vh",
      marginLeft: "-43px",
      marginRight: "0px",
    },
  },
  gridList: {
    flexWrap: "nowrap",
    transform: "translateZ(0)",
    overflow: "hidden",
    scrollBehavior: "smooth",
    width: "75vw",
    height: "100%",
  },
  title: {
    color: theme.palette.primary.light,
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)",
  },
  gridListTile: {
    height: "100% !important",
    padding: "0 !important",
    width: "100%",
    backgroundSize: "cover",

    "@media(max-width:426px)": {
      display: "none",
    },
    "& video": {},
  },

  image: {
    width: "100%",
    height: "100%",
    display: "block",
    margin: "auto",
    objectFit: "contain",
    [theme.breakpoints.up("md")]: {
      width: "100vw",
    },
  },
  video: {
    width: "100%",
    height: "100%",
  },
  nextButton: {
    position: "absolute",
    bottom: "50%",
    zIndex: 3,
    left: "90.5%",
    transform: " translate(0, 50%)",
    backgroundColor: "#073762",
    [theme.breakpoints.down("xs")]: {
      display: "none",
    },
  },
  backButton: {
    position: "absolute",
    top: "50%",
    zIndex: 1,
    right: "90.5%",
    transform: " translate(0, -50%)",
    backgroundColor: "#073762",
    "&:hover": {
      // backgroundColor: "#007bff",
    },
    [theme.breakpoints.down("xs")]: {
      display: "none",
    },
  },
  indexCircle: {
    transform: "translate(-50%, -100%)",
    top: "100%",
    left: "50%",
    position: "sticky",
    display: " grid",
    gap: "10px",
    [theme.breakpoints.down("xs")]: {
      display: "none",
    },
  },
  indexCircleButton: {
    padding: "15px 5px",
    height: "fit-content",
    background: "none",
    zIndex: "3",
    border: "none",
  },
  socialIcons: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: " 45px 0 0 0",
    gap: "31px",
    "& img": {
      width: "30px",
    },
    [theme.breakpoints.down("sm")]: {
      display: "none",
    },
  },
  iconz: {
    width: "20px",
    height: "20px",
